export type HTTPResponse = HTTPResponseFile | HTTPResponseString | HTTPResponseXML;
export type SvcResponse = HTTPResponse | XMLDocument | string | number | undefined;

export type SvcFun = (a0: any, a1?: any, a2?: any, a3?: any) => SvcResponse;
export type Service = {};

export function Service(fun: SvcFun, name?: string): any;
export function HTTPResponseFile(path: string): HTTPResponseFile;
export function HTTPResponseString(string: string): HTTPResponseString;
export function HTTPResponseXML(xml: any): HTTPResponseXML;
export function HTTPResponseHop(xml: any): HTTPResponseHop;
export function HTTPResponseAsync(xml: any): HTTPResponseAsync;
export function HTTPResponseProxy(xml: any): HTTPResponseProxy;
export function HTTPResponseAuthenticate(xml: any): HTTPResponseAuthenticate;
export function HTTPResponseJSON(xml: any): HTTPResponseJSON;
export function HTTPResponseError(xml: any): HTTPResponseError;

