"use hopscript";

/*---------------------------------------------------------------------*/
/*    imports                                                          */
/*---------------------------------------------------------------------*/
const _csv = require( "./_csv.hop" );
const fs = require( "fs" );

/*---------------------------------------------------------------------*/
/*    load                                                             */
/*---------------------------------------------------------------------*/
exports.load = _csv.load;

/*---------------------------------------------------------------------*/
/*    Symbol.compiler ...                                              */
/*---------------------------------------------------------------------*/
exports[ Symbol.compiler ] = (file, options) => {
   const val = _csv.load( file, options );
   
   if( options && options.target ) {
      const fd = fs.openSync( options.target, "w" );
      try {
	 const buf = JSON.stringify( val );
	 fs.write( fd, buf, 0, buf.length );
	 
	 return {
	    type: "filename",
	    value: options.target,
	 }
      } finally {
	 fs.closeSync( options.target );
      }
   } else {
      return {
	 type: "value",
	 value: val,
      }
   }
}
