export class Exif {
   filename: string;
   version: string;
   software: string;

   comment: string | undefined;
   model: string;
   make: string;
   
   date: Date;

   width: number;
   height: number;
   ewidth: number;
   eheight: number;

   iso: number;
   fnumber: [ number, number ];
   aperture: [ number, number ];
   maxAperture: [ number, number ];
   shutterSpeedValue: [ number, number ];
   exposureTime: [ number, number ];
   exposureProgram: number | false;
   exposureBias: number | false;
   brightnessValue: [ number, number ] | false;
   
   focalLength: [ number, number ] | false;
   subjectDistance: [ number, number ] | false;
   colorspace: number | false;
   sensingMethod: [ number, number ] | false;
   compositeImage: string | false;
   
   lensModel: string | false;
   lensMake: string | false;
   
   gpsTag: number | false;

   thumbnail: string | false;
   
   constructor(filename: string);
}


