export class Exif {
   filename: string;

   version: string;
   comment: string | undefined;
   model: string;
   make: string;
   date: Date;

   width: number;
   height: number;
   ewidth: number;
   eheight: number;

   iso: number;
   fnumber: [ number, number ];
   aperture: [ number, number ];
   shutterSpeedValue: [ number, number ];
   exposureTime: [ number, number ];
   exposureProgram: number | false;
   
   focalLength: [ number, number ];

   gpsTag: number;
   
   constructor(filename: string);
}


