/*---------------------------------------------------------------------*/
/*    The module                                                       */
/*---------------------------------------------------------------------*/
import { EXIF } from "exif-js";

export { Exif };

/*---------------------------------------------------------------------*/
/*    knownTags                                                        */
/*---------------------------------------------------------------------*/
const knownTags = ["orientation"];

/*---------------------------------------------------------------------*/
/*    Exif ...                                                         */
/*---------------------------------------------------------------------*/
function Exif(filename) {
   const exif = EXIF.readFromBinaryFile(filename);
   knownTags.forEach(t => Object.defineProperty(exif, t, { get: () => exif.getTab(t) }));
   return exif;
}
   
