/*---------------------------------------------------------------------*/
/*    Global constants                                                 */
/*---------------------------------------------------------------------*/
export const isServer: boolean;

/*---------------------------------------------------------------------*/
/*    Config                                                           */
/*---------------------------------------------------------------------*/
export interface Config {
   version: string;
   builid: string;
   hostname: string;
   engine: string;
   ports: { http: number | undefined , https: number | undefined };
   configDirectory: string;
   cacheDirectory: string;
   userdb: string | undefined;
   motd: string;
   verbose: number;
   charset: string;
   security: {
      realm: string;
      serviceAuthentication: boolean
   }
}

/*---------------------------------------------------------------------*/
/*    CommandLineArgs                                                  */
/*---------------------------------------------------------------------*/
export interface CommandLineArgs {
   "config-dir"?: string;
   p?: number;
   "http-port"?: number;
   s?: number;
   "https-port"?: number;
   v?: number;
   userdb?: string;
   q: undefined;
   "config"?: string;
}

/*---------------------------------------------------------------------*/
/*    init ...                                                         */
/*---------------------------------------------------------------------*/
export function init(args?: undefined | CommandLineArgs): Config;

/*---------------------------------------------------------------------*/
/*    parseCommandLine ...                                             */
/*---------------------------------------------------------------------*/
export function parseCommandLine(argv: string[]): CommandLineArgs;

/*---------------------------------------------------------------------*/
/*    config ...                                                       */
/*---------------------------------------------------------------------*/
export function config(): Config;

/*---------------------------------------------------------------------*/
/*    Resolver ...                                                     */
/*---------------------------------------------------------------------*/
export class Resolver {
    constructor(root: string, ...modules: string[]);
    register(specifier: any, parentURL?: string): void;
    resolve(mod: string): string;
}

/*---------------------------------------------------------------------*/
/*    Service ...                                                      */
/*---------------------------------------------------------------------*/
export function Service(fun: (...args: any) => any, optOrUrl: any): (...args) => Frame;

/*---------------------------------------------------------------------*/
/*    ServiceImpl ...                                                  */
/*---------------------------------------------------------------------*/
export class ServiceImpl {
    static postResponse(req: any, res: any): void;
    static putResponse(req: any, res: any): void;
    static getResponse(req: any, res: any): void;
    constructor(fun: (...args: any) => any, optOrUrl: any);
    dollar(): string;
}

/*---------------------------------------------------------------------*/
/*    Frame ...                                                        */
/*---------------------------------------------------------------------*/
export class Frame {
   constructor(svc: ServiceImpl, args: any[]);
}

/*---------------------------------------------------------------------*/
/*    ResponseHop                                                      */
/*---------------------------------------------------------------------*/
export interface HTTPResponseHeader {
   connection?: string;
   charset?: string;
   contentLength?: number;
   header?: any;
}
   
/*---------------------------------------------------------------------*/
/*    Response ...                                                     */
/*---------------------------------------------------------------------*/
export class Response {
    constructor(val: any, status?: number, headers?: any);
    hopResponse(req: any, res: any): void;
}
export function HTTPResponseString(str: any, opt?: HTTPResponseHeader): Response;
export function HTTPResponseAuthentication(opt?: HTTPResponseHeader): Response;
export function HTTPResponseFile(path: any, opt?: HTTPResponseHeader): Response;

/*---------------------------------------------------------------------*/
/*    HTML tags                                                        */
/*---------------------------------------------------------------------*/
declare class XmlElement {
    static attrsToString(attrs: any): string;
    static toResponse(n: any): any;
    constructor(tag: any, attrs: any, nodes: any);
    tag: any;
    attrs: any;
    nodes: any;
    tostring: boolean;
    toString(): boolean;
    toResponse(): boolean;
}

declare class HtmlElement extends XmlElement {
    mimetype: string;
}

declare class Tilde extends XmlElement {
}

interface XmlKeyboardEvents {
   onkeypress?: Tilde;
   onkeydown?: Tilde;
   onkeyup?: Tilde;
}

interface XmlMouseEvents {
   onclick?: Tilde;
   ondblclick?: Tilde;
   onmousedown?: Tilde;
   onmousemove?: Tilde;
   onmouseout?: Tilde;
   onmouseover?: Tilde;
   onmouseup?: Tilde;
   onmousewheel?: Tilde;
   onwheel?: Tilde;
}

interface XmlLocation {
   filename: string;
   pos: number;
   name: string;
}

interface XmlAttrs extends XmlKeyboardEvents, XmlMouseEvents {
   class?: string;
   id?: string;
   [propName: string]: string | Tilde | XmlLocation;
}
   
