/*---------------------------------------------------------------------*/
/*    Config ...                                                       */
/*---------------------------------------------------------------------*/
export function Config(obj) {
   return obj;
}

/*---------------------------------------------------------------------*/
/*    configGet ...                                                    */
/*---------------------------------------------------------------------*/
export function configGet(conf, k, def = undefined) {
   if (k in conf) {
      return conf[k];
   } else {
      return def;
   }
}

/*---------------------------------------------------------------------*/
/*    configAdd$ ...                                                   */
/*---------------------------------------------------------------------*/
export function configAdd$(key, value, conf) {
   conf[key] = value;
   return conf;
}

/*---------------------------------------------------------------------*/
/*    confMaxInt ...                                                   */
/*---------------------------------------------------------------------*/
export function confMaxInt(conf) {
   return configGet(conf, "int-size", 1 << 52);
}

/*---------------------------------------------------------------------*/
/*    confMinInt ...                                                   */
/*---------------------------------------------------------------------*/
export function confMinInt(conf) {
   return configGet(conf, "int-size", -1 << 53);
}
