"use hopscript"

var hop = require( "hop" );
var md = require( "markdown" );

/*---------------------------------------------------------------------*/
/*    generators ...                                                   */
/*---------------------------------------------------------------------*/
const generators = {
   "markdown": md.toMarkdown
}

/*---------------------------------------------------------------------*/
/*    compileFile ...                                                  */
/*---------------------------------------------------------------------*/
function compileFile( path, format ) {
   console.log( "cmpfile path=", path );
   const generator = generators[ format ] ? generators[ format ] : md.toMarkdown;
   console.log( "generator=", generator );
   const xml = md.parseFile( path, hop.locale, GLOBAL );
}

/*---------------------------------------------------------------------*/
/*    exports                                                          */
/*---------------------------------------------------------------------*/
exports.includeCode = function( path ) { return path; };
exports.seeAlso = function( keywords ) { return "" };

