"use strict";

/*---------------------------------------------------------------------*/
/*    Vector                                                           */
/*---------------------------------------------------------------------*/
export function Vector(len) {
   const a = new Array(len);

   Object.defineProperty(a, "length", {
      writable: false,
   } );
 
   for (let i = 0; i < len; i++) {
      a[i] = undefined;
   }
   Object.seal(a);
   
   return a;
}


console.log("hopscript.js", new Vector(20));
