type sqliteCallbackSuccess = (err: null) => void;
type sqliteCallbackFailure = (err: any) => void;
type sqliteCallback =  sqliteCallbackSuccess | sqliteCallbackFailure;

export interface Database {
   close: (cb?: sqliteCallback) => void;
}

export declare function Database(filename: string, mode?: number, callback?: sqliteCallback): Database

