"use hopscript"

/*---------------------------------------------------------------------*/
/*    Native implementation                                            */
/*---------------------------------------------------------------------*/
var system = require( "./_system.hop" );

/*---------------------------------------------------------------------*/
/*    stringToCmd ...                                                  */
/*    -------------------------------------------------------------    */
/*    Split a string into a an of string.                              */
/*---------------------------------------------------------------------*/
function stringToCmd( cmd ) {
   if( typeof cmd === "string" ) {
      return cmd
      	 .match( /([^ "']+)|"([^"]*)"|'([^']*)'|(?:[ \t]+)/g )
      	 .filter( s => !s.match( /^[ \t]+$/ ) )
      	 .map( s => s[ 0 ] === "'" || s[ 0 ] === '"'
 	       	  ? s.substring( 1, s.length - 1 )
	       	  : s );
   } else {
      return cmd;
   }
}

/*---------------------------------------------------------------------*/
/*    system ...                                                       */
/*---------------------------------------------------------------------*/
export function system( cmd ) {
   return new Promise( (resolve, reject) => {
		system.system( stringToCmd( cmd ), resolve, reject );
	     } );
}

/*---------------------------------------------------------------------*/
/*    systemSync ...                                                   */
/*---------------------------------------------------------------------*/
export function systemSync( cmd ) {
   return system.systemSync( stringToCmd( cmd ) );
}

/*---------------------------------------------------------------------*/
/*    exports                                                          */
/*---------------------------------------------------------------------*/
exports.system = system;
exports.systemSync = systemSync;
