type encoding = "utf-8" | "latin-1";
type callback = (err, obj) => void;

export type author = [string, string] | [string];
	    
export interface BibEntry {
   id: string,
   title: string,
   authors: author[],
   type: string,
   year?: string | number,
   month?: string,
   url?: string,
   address?: string,
   volume?: number,
   journal?: string
   doi?: string
}
       
export declare function bibtexParse(fd: number, cb: callback) : void;
export declare function bibtexParse(fd: number) : Promise<any[]>;
export declare function bibtexParseSync(fd: number) : any[];

export declare function bibtexParseFile(file: string, cb: callback) : void;
export declare function bibtexParseFile(file: string) : Promise<any[]>;
export declare function bibtexParseFileSync(file: string) : any[];

export declare function bibtexParseString(entry: string, enc?: encoding) : BibEntry[];
