"use hopscript";

/*---------------------------------------------------------------------*/
/*    imports                                                          */
/*---------------------------------------------------------------------*/
const _text = require( "./_text.hop" );

/*---------------------------------------------------------------------*/
/*    bibtexParse ...                                                  */
/*---------------------------------------------------------------------*/
function bibtexParse(fd, cb) {
   return _text.bibtexParse(fd, cb);
}

function bibtexParseSync(fd) {
   return _text.bibtexParseSync(fd);
}

/*---------------------------------------------------------------------*/
/*    bibtexParseFile ...                                              */
/*---------------------------------------------------------------------*/
function bibtexParseFile(file, cb) {
   return _text.bibtexParseFile(file, cb);
}

function bibtexParseFileSync(file) {
   return _text.bibtexParseFileSync(file);
}

/*---------------------------------------------------------------------*/
/*    bibtexParseString ...                                            */
/*---------------------------------------------------------------------*/
function bibtexParseString(entry, encoding = "utf8") {
   return _text.bibtexParseString(entry, encoding);
}

/*---------------------------------------------------------------------*/
/*    exports                                                          */
/*---------------------------------------------------------------------*/
export { bibtexParse, bibtexParseSync };
export { bibtexParseFile, bibtexParseFileSync };
export { bibtexParseString };
