/* export declare namespace vcf {                                      */
   interface email {
      label: string;
      replyto?: string;
      email: string;
   }

   interface phone {
      label: string;
      number: string;
   }
   
   interface address {
      label: string;
      pobox?: string;
      ext?: string;
      street?: string;
      city?: string;
      region?: string;
      zip?: string;
      country?: string;
   }

   interface photo {
      encoding: string;
      format: string;
      data: string;
   }
   
   export declare class vCard {
	  version: string;
      	  uid: string;
      	  familyname?: string;
      	  firstname?: string;
          nickname?: string;
       	  emails: email[];
      	  phones: phone[]
      	  addresses: address[];
      	  photo?: photo;
	  url?: string;
      	  birthday?: string;
      	  sound?: string;
      	  lang?: string;
      	  related?: string;
      	  color?: string;
      	  thumbnail?: string;
      	  key?: string;
      	  notes: string[];
      	  extras: any;
   }

   export function loadVCF(file: string, options?): vCard[];
   export function loadBBDB(file: string, options?): vCard[];
   export function load(file: string, options?): vCard[];

   export function toVCARD(vcard: vCard): string;
   export function toBBDB(vcard: vCard): string;

   export function stringify(vcard: vCard, format: string): string;
/* }                                                                   */
	  
