export interface xmlElement {
   tag: string;
   atributes: Record<string, string|number>;
   children: any[];
}

export type xmlValue = xmlElement | string | number;
	    
export declare function parseSync(i: string | number): xmlValue[];

type xmlCallbackSuccess = (err: boolean, val: xmlValue[]) => void;
type xmlCallbackFailure = (err: any, val: undefined) => void;
type xmlCallback =  xmlCallbackSuccess | xmlCallbackFailure;
						 
export declare function parse(i: string | number, cb: xmlCallback): void;
export declare function parse(i: string | number): Promise<xmlValue[]>;
