"use hopscript";

/*---------------------------------------------------------------------*/
/*    ES6 module                                                       */
/*---------------------------------------------------------------------*/
export { createElement, load, parseSync, parse };

/*---------------------------------------------------------------------*/
/*    imports                                                          */
/*---------------------------------------------------------------------*/
const _xml = require( "./_xml.hop" );

/*---------------------------------------------------------------------*/
/*    createElement                                                    */
/*---------------------------------------------------------------------*/
const createElement = _xml.createElement;
exports.createElement = createElement;

/*---------------------------------------------------------------------*/
/*    load                                                             */
/*---------------------------------------------------------------------*/
const load = _xml.load;
exports.load = load;

/*---------------------------------------------------------------------*/
/*    parse                                                            */
/*---------------------------------------------------------------------*/
const parseSync = _xml.parseSync;
const parse = _xml.parse;
exports.parse = parse;

/*---------------------------------------------------------------------*/
/*    Symbol.compiler ...                                              */
/*---------------------------------------------------------------------*/
exports[ Symbol.compiler ] = (file, options) => {
   return { type: "value", value: _xml.load( file, options ) }
}
